property.code:
define('inlineDiagramActionsMessage', {
  prefix: 'inlineDiagram.actions.',
  keys: [
    'save',
    'created',
    'updated',
    'error'
  ]
});

require(['jquery', 'xwiki-l10n!inlineDiagramActionsMessage'], function ($, l10n) {
  const urlParams = new URLSearchParams(window.location.search);
  const returnDocumentStringRef = urlParams.get('returnDocument');
  const sourceDocumentStringRef = urlParams.get('source');
  const diagramName = urlParams.get('diagramName');
  const returnFragment = urlParams.get('returnFragment');
  const saveDiagramContent = `${XWiki.contextPath}/rest/diagram/inline/save/${encodeURIComponent(sourceDocumentStringRef)}/${encodeURIComponent(diagramName)}`;
  const saveDiagramRendering = `${XWiki.contextPath}/rest/diagram/inline/saveRender/${encodeURIComponent(sourceDocumentStringRef)}/${encodeURIComponent(diagramName)}`;
  let notification = null;

  let savedSuccessfully = function (response) {
    // if response code is 201, it means the diagram was created, otherwise it was updated
    if (response.status === 201) {
      notification.replace(new XWiki.widgets.Notification(l10n['created'], "done"));
    } else {
      notification.replace(new XWiki.widgets.Notification(l10n['updated'], "done"));
    }
  };

  let savedFailed = function (error) {
    notification.replace(new XWiki.widgets.Notification(l10n['error'], "error"));
  };

  let saveDiagram = function () {
    notification = new XWiki.widgets.Notification(l10n['save'], "inprogress")
    let diagramFile = window._xfiles[0];
    // Stop editing the file.
    diagramFile.ui.editor.graph.stopEditing(false);
    // Update the file with the latest changes.
    diagramFile.updateFileData();
    // Get the updated file.
    let diagramContent = window._xfiles[0].getData();
    $.ajax({
      url: saveDiagramContent,
      method: "POST",
      contentType: "text/plain; charset=UTF-8",
      data: diagramContent,
      processData: false
    })
    .done(function (data, textStatus, jqXHR) {
      // After the critical XML was successfully saved we also save a png for of the first page, but we don't wait for
      // it.
      window._xfiles[0].getUi().getEmbeddedPng(function(png) {$.ajax({
        url: saveDiagramRendering,
        method: "POST",
        contentType: "text/plain; charset=UTF-8",
        data: png,
        processData: false
        })}, null, null, 1, null);
        savedSuccessfully(jqXHR);
        console.log("Save finished");
    })
    .fail(function (jqXHR) {
      savedFailed(jqXHR);
    });
  };

  let returnToDocument = function () {
    window.onbeforeunload = null;
    let returnDocumentReference = XWiki.Model.resolve(returnDocumentStringRef, XWiki.EntityType.DOCUMENT);
    let url = new XWiki.Document(returnDocumentReference).getURL('view', '', returnFragment);
    window.location = url;
  };

  $('#diagram-inline-save').on('click', saveDiagram);

  $('#diagram-inline-cancel').on('click', returnToDocument);
});

